configuration.source:
System.out.println("******** Going to make release zip ********")
deployFolder = "${project.build.directory}/deployoutput" deployUnzip = "${project.build.directory}/deployunzip" outfileName = "${package.name}-${project.version}.zip" ant.delete(dir: "${deployFolder}") ant.mkdir(dir: "${deployFolder}")
ant.delete(dir: "${deployUnzip}") ant.mkdir(dir: "${deployUnzip}")
ant.delete(dir: "${deployUnzip}/lib") ant.mkdir(dir: "${deployUnzip}/lib")
ant.delete(dir: "${deployUnzip}/open-cli-schema") ant.mkdir(dir: "${deployUnzip}/open-cli-schema")
ant.delete(dir: "${deployUnzip}/open-cli-sample") ant.mkdir(dir: "${deployUnzip}/open-cli-sample")
ant.delete(dir: "${deployUnzip}/data") ant.mkdir(dir: "${deployUnzip}/data")
ant.delete(dir: "${deployUnzip}/data/tmp") ant.mkdir(dir: "${deployUnzip}/data/tmp")
ant.delete(dir: "${deployUnzip}/script") ant.mkdir(dir: "${deployUnzip}/script")
ant.copy(todir: "${deployUnzip}") { fileset(dir: "${basedir}/src/main/release"){ exclude(name: "**/.gitignore") } }
ant.copy(todir: "${deployUnzip}") { fileset(file: "${basedir}/installer/install.sh") } ant.chmod(file: "${deployUnzip}/bin/oclip.sh", perm: "ugo+x") ant.chmod(file: "${deployUnzip}/install.sh", perm: "ugo+x")
ant.copy(todir: "${deployUnzip}/lib") { fileset(file: "${project.build.directory}/../../../main/target/cli-main-${project.version}.jar") fileset(dir: "${project.build.directory}/../../../main/target/lib/") fileset(dir: "${project.build.directory}/../../../products/target/lib/") fileset(dir: "${project.build.directory}/../../../profiles/target/lib/") fileset(dir: "${project.build.directory}/../../../grpc/target/lib/") }
ant.copy(todir: "${deployUnzip}/conf") { fileset(dir: "${project.build.directory}/../../../products/target/conf/", erroronmissingdir: false) fileset(dir: "${project.build.directory}/../../../profiles/target/conf/", erroronmissingdir: false) fileset(file: "${basedir}/src/main/release/conf/*.properties") }
ant.copy(todir: "${deployUnzip}/script") { fileset(dir: "${project.build.directory}/../../../products/target/script/", erroronmissingdir: false) }
ant.copy(todir: "${deployUnzip}/docs") { fileset(file: "${project.build.directory}/../../../README.md") }
ant.zip(destfile: "${deployFolder}/${outfileName}") { zipfileset(dir: "${deployUnzip}", excludes: "**/*.sh") zipfileset(dir: "${deployUnzip}", includes: "**/*.sh", filemode: "755") } System.out.println("******** completed. ************")
